function renderContact(contact) {
  const card = document.createElement("div");
  card.classList.add("card");
  card.dataset.id = contact.id;


  const deleteButton = document.createElement("button");
  deleteButton.classList.add("deleteBtn");
  deleteButton.title = "Delete this contact";
  deleteButton.innerText = "X";
  deleteButton.addEventListener("click", () => {
    deleteContact(contact.id);
    render(contacts);
  });

  const avatar = document.createElement("div");
  avatar.classList.add("avatar");

  const circle1 = document.createElement("div");
  circle1.classList.add("circle");
  avatar.appendChild(circle1);

  const circle2 = document.createElement("div");
  circle2.classList.add("circle");
  avatar.appendChild(circle2);

  const img = document.createElement("img");
  img.src = contact.picture;
  avatar.appendChild(img);

  const info = document.createElement("div");
  info.classList.add("info");

  const name = document.createElement("span");
  name.classList.add("name", "big");
  name.innerText = contact.name;
  info.appendChild(name);

  const email = document.createElement("span");
  email.classList.add("email", "small");
  email.innerText = contact.email;
  info.appendChild(email);

  const details = document.createElement("div");
  details.classList.add("details");

  const phone = document.createElement("div");
  phone.classList.add("phone");
  phone.innerText = contact.phone;
  details.appendChild(phone);

  const website = document.createElement("div");
  website.classList.add("website");
  website.innerText = contact.website;
  details.appendChild(website);

  const additional = document.createElement("div");
  additional.classList.add("additional");

  const address = document.createElement("div");
  address.classList.add("address");

  const suite = document.createElement("div");
  suite.classList.add("suite");
  suite.innerText = contact.address.suite;
  address.appendChild(suite);

  const street = document.createElement("div");
  street.classList.add("street");
  street.innerText = contact.address.street;
  address.appendChild(street);

  const city = document.createElement("div");
  city.classList.add("city");
  city.innerText = `${contact.address.city}, ${contact.address.zipcode}`;
  address.appendChild(city);

  additional.appendChild(address);

  const company = document.createElement("div");
  company.classList.add("company");

  const label = document.createElement("div");
  label.classList.add("label");
  label.innerText = "Works at";
  company.appendChild(label);

  const companyName = document.createElement("div");
  companyName.classList.add("company-name");
  companyName.innerText = contact.company.name;
  company.appendChild(companyName);

  additional.appendChild(company);

  card.appendChild(deleteButton);
  card.appendChild(avatar);
  card.appendChild(info);
  card.appendChild(details);
  card.appendChild(additional);

  return card;
}

function loadCities(contacts) {
  const select = document.getElementById("filterOptions");
  select.innerHTML = "";

  const uniqueCities = new Set();
  for (const contact of contacts) {
    uniqueCities.add(contact.address.city);
  }

  const defaultOption = document.createElement("option");
  defaultOption.value = "0";
  defaultOption.innerText = "-- Select a city --";
  select.appendChild(defaultOption);

  for (const city of uniqueCities) {
    const option = document.createElement("option");
    option.value = city;
    option.innerText = city;
    select.appendChild(option);
  }
}

function render(contacts) {
  const contactSection = document.getElementById("contacts");
  contactSection.innerHTML = "";

  if (!contacts || contacts.length === 0) {
    return;
  }

  for (const contact of contacts) {
    const contactCard = renderContact(contact);
    contactSection.appendChild(contactCard);
  }

  loadCities(contacts);
}


function filterByCity(city) {
  const filteredContacts = contacts.filter(
    (contact) => contact.address.city === city
  );
  return filteredContacts;
}

function filterHandler() {
  const select = document.getElementById("filterOptions");
  const selectedCity = select.value;

  if (selectedCity === "0") {
    render(contacts);
  } else {
    const filteredContacts = filterByCity(selectedCity);
    render(filteredContacts);
  }
}

function deleteContact(id) {
  const index = contacts.findIndex((contact) => contact.id === id);
  if (index !== -1) {
    contacts.splice(index, 1);
  }
}

function deleteButtonHandler() {
  const deleteButtons = document.querySelectorAll(".deleteBtn");
  deleteButtons.forEach((button) => {
    button.addEventListener("click", (event) => {
      const card = event.target.closest(".card");
      const id = parseInt(card.dataset.id);
      const updatedContacts = contacts.filter((contact) => contact.id !== id);
      contacts = updatedContacts;
      render(contacts);
    });
  });
}


function main() {
  const select = document.getElementById("filterOptions");
  select.addEventListener("change", filterHandler);

  deleteButtonHandler();

  render(contacts);
}

main();




